using AEAssist;
using AEAssist.CombatRoutine;
using AEAssist.CombatRoutine.Module;
using AEAssist.CombatRoutine.Trigger;
using AEAssist.CombatRoutine.Trigger.Node;
using AEAssist.Extension;
using AEAssist.MemoryApi;
using Aki.Samurai;

namespace Aki.timelinetriggers.DOT
{
    public class TriggerAction_DOT : ITriggerScript
    {
        public bool Check(ScriptEnv scriptEnv, ITriggerCondParams condParams)
        {
            int count = 0;//彼岸花之前有几个G

            if (Core.Resolve<MemApiBuff>().GetAuraTimeleft(Core.Me, Data.AurasDefine.斩浪预备, true) > 5500) //5.5秒以上斩浪预备
            { count += 2; }
            if (Core.Resolve<MemApiSpell>().CheckActionChange(Data.SpellsDefine.OgiNamikiri) == Data.SpellsDefine.KaeshiNamikiri)//回返斩浪
            { count += 1; }

            if (SAMHelper.闪Count() == 3)//三闪
            {
                if (Core.Me.HasAura(Data.AurasDefine.普通雪月花回返) ||
                    Core.Me.HasAura(Data.AurasDefine.强化雪月花回返))
                { count += 4; }
                else
                { count += 3; }

                if (SAMHelper.下个连击阶段() is 2 or 3)
                { count -= 1; }
            }

            if (SAMHelper.闪Count() == 2)//双闪
            {
                if (Core.Me.HasAura(Data.AurasDefine.普通雪月花回返) ||
                    Core.Me.HasAura(Data.AurasDefine.强化雪月花回返))
                { count += 1; }

                if (SAMHelper.下个连击阶段() == 3)
                { count += 4; }

                if (SAMHelper.下个连击阶段() == 2)
                { count += 5; }

                if (SAMHelper.下个连击阶段() == 1)
                { count += 6; }
            }

            if (SAMHelper.闪Count() == 1)//单闪
            { count += 0; }

            if (SAMHelper.闪Count() == 0) //无闪
            {
                if (SAMHelper.下个连击阶段() == 3)
                { count += 1; }

                if (SAMHelper.下个连击阶段() == 2)
                { count += 1; }

                if (SAMHelper.下个连击阶段() == 1)
                { count += 2; }
            }


            if (count is 0 or 1)//打DOT不打AOE
            { AI.Instance.TriggerlineData.Variable["P2开场AOE"] = 2; }

            if (count is 2)//打DOT打AOE
            { AI.Instance.TriggerlineData.Variable["P2开场AOE"] = 1; }

            if (count is 3 or 4)//不打DOT不打AOE
            { AI.Instance.TriggerlineData.Variable["P2开场AOE"] = 2; }

            if (count is 5)//不打DOT打AOE
            { AI.Instance.TriggerlineData.Variable["P2开场AOE"] = 1; }

            if (count is 0 or 1 or 2) 
                return true;
            return false;
        }
    }
}

using System.Numerics;
using AEAssist;
using AEAssist.CombatRoutine.Module.Target;
using AEAssist.CombatRoutine.Trigger;
using AEAssist.CombatRoutine.Trigger.Node;
using AEAssist.Extension;
using Dalamud.Game.ClientState.Objects.Types;

namespace BoxingBunny.Triggers
{
    public class TriggerScript_FRU_P4_AutoTarget : ITriggerScript
    {
        // 配置参数（可根据需要进行调整）
        // 当两个目标血量百分比差值超过该值时，触发目标切换，单位为百分比。
        private const float AcceptablePercentage = 3f;
        // 切换目标的最小间隔，单位为毫秒。
        private const int Interval = 1000;
        // 若目标血量低于此百分比，则视为血量过低，不进行目标切换，单位为百分比。
        private const float LowHpPercentage = 1f;
        // 防抖动阈值，当较低血量百分比与上一次记录值的变化超过该值时，认为目标状态有足够变化，从而触发目标切换，单位为百分比。
        private const float DebounceThreshold = 0.1f;

        // 记录上一次最小血量百分比以及最后一次切换时间
        private float _lastMinPercentage;
        private DateTime _lastSwitchTime = DateTime.MinValue;

        private static IBattleChara? Darkness =>
            TargetMgr.Instance.Enemys.Values.FirstOrDefault(o =>
                o.DataId == 17835 &&
                o.IsTargetable &&
                (!Core.Me.IsMelee() && !Core.Me.IsTank() || Vector3.Distance(o.Position, Core.Me.Position) <= 3.5f + o.HitboxRadius + 3));

        private static IBattleChara? Frost =>
            TargetMgr.Instance.Enemys.Values.FirstOrDefault(o =>
                o.DataId == 17833 &&
                o.IsTargetable &&
                (!Core.Me.IsMelee() && !Core.Me.IsTank() || Vector3.Distance(o.Position, Core.Me.Position) <= 3.5f + o.HitboxRadius + 3));

        public bool Check(ScriptEnv scriptEnv, ITriggerCondParams condParams)
        {
            // 如果玩家正在施法，则不切换目标
            if (Core.Me.IsCasting)
                return false;

            // 控制切换频率
            if ((DateTime.Now - _lastSwitchTime).TotalMilliseconds < Interval)
                return false;

            // 获取两个目标
            var dark = Darkness;
            var light = Frost;

            // 如果两个目标都不存在，则不做处理
            if (dark == null && light == null)
                return false;

            // 如果两个目标都存在且血量都过低，则不切换
            if (dark != null && light != null)
            {
                float darkHpPercent = (float)dark.CurrentHp / dark.MaxHp * 100f;
                float lightHpPercent = (float)light.CurrentHp / light.MaxHp * 100f;
                if (darkHpPercent < LowHpPercentage && lightHpPercent < LowHpPercentage)
                    return false;
            }

            // 如果只有盖娅
            if (dark != null && light == null)
            {
                if (Core.Me.GetCurrTarget()?.EntityId != dark.EntityId)
                    Core.Me.SetTarget(dark);
                _lastSwitchTime = DateTime.Now;
                return false;
            }

            // 如果只有琳
            if (dark == null && light != null)
            {
                if (Core.Me.GetCurrTarget()?.EntityId != light.EntityId)
                    Core.Me.SetTarget(light);
                _lastSwitchTime = DateTime.Now;
                return false;
            }

            // 两个目标都存在时
            if (dark != null && light != null)
            {
                float darkPercent = (float)dark.CurrentHp / dark.MaxHp * 100f;
                float lightPercent = (float)light.CurrentHp / light.MaxHp * 100f;
                float minPercentage = MathF.Min(darkPercent, lightPercent);
                float maxPercentage = MathF.Max(darkPercent, lightPercent);

                // 判断是否需要切换目标：
                // 1. 当前目标为空；
                // 2. 两目标血量差值超过 AcceptablePercentage；
                // 3. 较低血量百分比与上次记录变化超过防抖动阈值。
                if (Core.Me.GetCurrTarget() == null ||
                    maxPercentage - minPercentage > AcceptablePercentage ||
                    MathF.Abs(minPercentage - _lastMinPercentage) > DebounceThreshold)
                {
                    _lastMinPercentage = minPercentage;

                    // 当血量差值超出 AcceptablePercentage 时，切换到血量较高的目标
                    if (maxPercentage - minPercentage > AcceptablePercentage)
                    {
                        IBattleChara target = darkPercent > lightPercent ? dark : light;
                        Core.Me.SetTarget(target);
                    }
                }
            }

            _lastSwitchTime = DateTime.Now;
            return false;
        }
    }
}
